class StylesPanel extends React.Component {
    state = {
        
    }



    render() {
        var wepType = null;
        if (this.props.weapon == null) {
            wepType = "unarmed"
        } else {
            wepType = this.props.weapon.type
        }
        var atkStyles = attackstyles[wepType].map(attackstyle => {
            return <AttackStyle 
                    // initialSet={this.selectedOptionSetHandler} 
                    selectHandler={this.props.selectHandler} 
                    selected={this.props[attackstyle.opt]} 
                    name={attackstyle.name} 
                    style={attackstyle.style} 
                    opt={attackstyle.opt} 
                    rsrc={attackstyle.rsrc}/>
        })
        return (
            <>
            <div className="wepname">{this.props.weapon == null ? "Unarmed" : this.props.weapon.label }</div>
            <div className="stylespanel">
                {atkStyles}
            </div>
            </>
        )
    }
}

export default StylesPanel